  rotatable: true,
  width: 100,
  height: 100,
  top: 0,
  left: 0,
  equalProportion: true,
  border: false,
  w_pos: 0.3,
  w_neg: 1.0,
  t_i: 0.3
}
const itemList = ref([])

const router = useRouter()
const projectInfo = ref({
  width: 500,
  height: 200,
