        val = parseFloat(val.toFixed(1))
        if (val > 1 || val < 0) {
            toast.add({ severity: 'warn', summary: 'Warning', detail: '影响因子范围为[0,1]', life: 3000 });
            return
        }
        itemList.value[selectedItemIndex.value].t_i = val
    }
})

